const http=require("http"),fs=require("fs"),mime=require("mime");function init(e,t){http.createServer(async function(e,t){var i;fs.existsSync("./public"+e.url)?(i=await readFileData(e.url),t.writeHead(200,{"Content-Type":mime.getType("./public"+e.url)}),t.end(i)):(t.writeHead(200,{"Content-Type":"text/html"}),t.end("Hello World\n"))}).listen(e).then(t(e))}async function readFileData(e){return new Promise((i,t)=>{try{fs.readFile("./public"+e,(e,t)=>{i(t)})}catch(e){t(e)}})}module.exports={init:init};